import React, { useState } from "react";
import { StyleSheet, Text, View, TouchableOpacity } from "react-native";
import {
  Input,
  Button,
  ListItem,
  Icon,
  Tab,
  TabView,
} from "react-native-elements";
import * as DocumentPicker from "expo-document-picker";

const TrainModel = () => {
  const [document, setDocument] = React.useState(null);
  const [expanded, setExpanded] = React.useState(false);
  const [index, setIndex] = React.useState(0);
  const pickDocument = async () => {
    let result = await DocumentPicker.getDocumentAsync({});
    alert(result.uri);
  };

  return (
    <View style={styles.container}>
      <Button
        containerStyle={styles.btn}
        title="Load Data"
        onPress={pickDocument}
      />
      <ListItem.Accordion
        content={
          <>
            <ListItem.Content>
              <ListItem.Title>Select Model</ListItem.Title>
            </ListItem.Content>
          </>
        }
        isExpanded={expanded}
        onPress={() => {
          setExpanded(!expanded);
        }}
      >
        <Tab value={index}>
          <Tab.Item title="recent" />
          <Tab.Item title="favorite" />
          <Tab.Item title="cart" />
        </Tab>

        <TabView value={index} onChange={setIndex}>
          <TabView.Item style={{ backgroundColor: "red", width: "100%" }}>
            <Text h1>Recent</Text>
          </TabView.Item>
          <TabView.Item style={{ backgroundColor: "blue", width: "100%" }}>
            <Text h1>Favorite</Text>
          </TabView.Item>
          <TabView.Item style={{ backgroundColor: "green", width: "100%" }}>
            <Text h1>Cart</Text>
          </TabView.Item>
        </TabView>
      </ListItem.Accordion>
      <ListItem.Accordion
        content={
          <>
            <ListItem.Content>
              <ListItem.Title>Train Model</ListItem.Title>
            </ListItem.Content>
          </>
        }
        isExpanded={expanded}
        onPress={() => {
          setExpanded(!expanded);
        }}
      >
        <ListItem.Accordion
          content={
            <>
              <ListItem.Content>
                <ListItem.Title>Prepare Training Data</ListItem.Title>
              </ListItem.Content>
            </>
          }
          isExpanded={expanded}
          onPress={() => {
            setExpanded(!expanded);
          }}
        >
          {/* items */}
        </ListItem.Accordion>
        <ListItem.Accordion
          content={
            <>
              <ListItem.Content>
                <ListItem.Title>Set HyperParams</ListItem.Title>
              </ListItem.Content>
            </>
          }
          isExpanded={expanded}
          onPress={() => {
            setExpanded(!expanded);
          }}
        >
          {/* items */}
        </ListItem.Accordion>
      </ListItem.Accordion>
    </View>
  );
};

export default TrainModel;

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flex: 1,
    backgroundColor: "#03091E",
    padding: 10,
  },
  btn: {
    marginBottom: 10,
  },
  disabled: {
    backgroundColor: "darkgrey",
  },
});
