import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  KeyboardAvoidingView,
} from "react-native";

import { connect } from "react-redux";
import PropTypes from "prop-types";

import { Input, Button, ListItem, CheckBox } from "react-native-elements";
import * as DocumentPicker from "expo-document-picker";
import AsyncStorage from "@react-native-async-storage/async-storage";
import mlapiService from "../services/mlapi.service";
import { postTest } from "../redux/actions/test";

const regressors = [
  {
    name: "LinearRegression",
  },

  {
    name: "SVR",
  },
  {
    name: "KNeighborsRegressor",
  },
  {
    name: "RandomForestRegressor",
  },
  {
    name: "GradientBoostingRegressor",
  },
  {
    name: "RandomForestRegressor",
  },
  {
    name: "GradientBoostingRegressor",
  },
];
const classifiers = [
  {
    name: "DecisionTreeClassifier",
  },
  {
    name: "LogisticRegression",
  },
  {
    name: "SVC",
  },

  {
    name: "GaussianNB",
  },
  {
    name: "KNeighborsClassifier",
  },

  {
    name: "RandomForestClassifier",
  },

  {
    name: "GradientBoostingClassifier",
  },
];
const clusters = [
  {
    name: "KMeans",
  },
];
function Taber(props) {
  const [type, setType] = React.useState(0);

  return (
    <View style={{}}>
      <View style={styles.taber}>
        <TouchableOpacity onPress={() => setType(0)}>
          <Text>Regression</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => setType(1)}>
          <Text>Classification</Text>
        </TouchableOpacity>
        <TouchableOpacity disabled={true} onPress={() => setType(2)}>
          <Text>Cluster</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.list__view}>
        <SafeAreaView>
          <ScrollView>
            {type === 0
              ? regressors.map((regr, index) => (
                  <TouchableOpacity
                    onPress={() => props.func(regr.name)}
                    style={styles.options}
                    key={index}
                  >
                    <Text>{regr.name}</Text>
                  </TouchableOpacity>
                ))
              : type === 1
              ? classifiers.map((regr, index) => (
                  <TouchableOpacity
                    onPress={() => props.func(regr.name)}
                    style={styles.options}
                    key={index}
                  >
                    <Text>{regr.name}</Text>
                  </TouchableOpacity>
                ))
              : clusters.map((regr, index) => (
                  <TouchableOpacity
                    onPress={() => props.func(regr.name)}
                    style={styles.options}
                    key={index}
                  >
                    <Text>{regr.name}</Text>
                  </TouchableOpacity>
                ))}
          </ScrollView>
        </SafeAreaView>
      </View>
    </View>
  );
}

const TrainModel = (props) => {
  const [model, setModel] = React.useState(null);

  const [document, setDocument] = React.useState(null);
  const [checked, setChecked] = React.useState(false);
  const [expanded, setExpanded] = React.useState(false);
  const [expanded1, setExpanded1] = React.useState(false);
  const [expanded2, setExpanded2] = React.useState(false);
  const [expanded3, setExpanded3] = React.useState(false);
  const pickDocument = async () => {
    let result = await DocumentPicker.getDocumentAsync({});
    alert(result.uri);
  };
  const propTypes = {
    postTest: PropTypes.func.isRequired,
    user_id: PropTypes.number.isRequired,
  };

  const handleLoadData = () => {
    const data = {
      user_id: 1, //SET OVER HERE
      model_name: model,
    };
    console.log(data);
    AsyncStorage.getItem("token")
      .then((token) => {
        const config = {
          headers: {
            "Content-Type": "application/json",
          },
        };
        if (token) {
          config.headers["Authorization"] = `bearer ${token}`;
        }
        mlapiService
          .selectModel(data, config)
          .then((res) => {
            props.postTest(res.data);
            console.log(res.data);
          })
          .catch((err) => {
            console.log(err);
          });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <KeyboardAvoidingView
      behavior="position"
      keyboardVerticalOffset={15}
      style={styles.container}
    >
      <Button
        containerStyle={styles.btn}
        title={"Load Data"}
        onPress={pickDocument}
      />
      <ListItem.Accordion
        content={
          <>
            <ListItem.Content>
              <ListItem.Title>Select Model</ListItem.Title>
            </ListItem.Content>
          </>
        }
        isExpanded={expanded}
        onPress={() => {
          setExpanded(!expanded);
        }}
      >
        <ListItem>
          <ListItem.Content>
            <Taber func={setModel} />
          </ListItem.Content>
        </ListItem>
      </ListItem.Accordion>
      <ListItem.Accordion
        content={
          <>
            <ListItem.Content>
              <ListItem.Title>Train Model</ListItem.Title>
            </ListItem.Content>
          </>
        }
